mapper.select:
select board_id, board_gbn, board_title, board_txt, hit_cnt, del_yn, in_date, in_user, (select user_name from t_user a where a.user_id = c.in_user) in_user_name, up_date, up_user, (select user_name from t_user b where b.user_id = c.up_user) up_user_name from t_board c where board_gbn = #{board_gbn} and del_yn = 'N'
AND (board_title LIKE '%${search_value}%' or board_txt LIKE '%${search_value}%')
ORDER BY board_id DESC LIMIT #{startNum},#{maxRows}

mapper.update:
update t_board 
set
	del_yn 	= 'Y',
	up_date 	= NOW() , 
	up_user 	= #{up_user}
where board_id	= #{board_id}

update t_board 
set
	del_yn 	= 'N',
	up_date 	= NOW() , 
	up_user 	= #{up_user}
where board_id	= #{board_id}
